public class AreaOfCircle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		float radius =5.6f,
				pie=3.14f;
		float area;
		
		area=pie*radius*radius;
		
		System.out.println("Area of Circle"+area);
		
	}

}
